  (1, 0, 13000, 13000, 'Administrator', ' ', '', 'administrator', '<p>System Administrator has ultimate control over all data, users, and extensions on the site. Use with care and protect against others accessing this account.</p>', 1, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 1, 0, 1, 18, 1, 0, '{\r\n"group_web_address":"",\r\n"group_email_address":"",\r\n"group_owner":"42",\r\n"group_collection_id":"0"\r\n}', '{}', '{}', 'en-GB', 0, 1),
  (2, 0, 13000, 13000, 'Public', ' ', '', 'public', '<p>All visitors, regardless of whether or not the visitor is logged on, are members of the <strong>Public</strong> group.</p>', 1, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 1, 0, 2, 7, 1, 0, '{\n"group_web_address":"",\n"group_email_address":"",\n"group_owner":"0",\n"group_collection_id":"0"\n}', '{}', '{}', 'en-GB', 0, 2),
  (3, 0, 13000, 13000, 'Guest', ' ', '', 'guest', '<p>Visitors who are not logged on are members of the <strong>Guest</strong> group.</p>', 1, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 1, 2, 3, 4, 2, 0, '{\r\n"group_web_address":"",\r\n"group_email_address":"",\r\n"group_owner":"0",\r\n"group_collection_id":"0"\r\n}', '{}', '{}', 'en-GB', 0, 3),
  (4, 0, 13000, 13000, 'Registered', ' ', '', 'registered', '<p>Visitors who are logged on are members of the <strong>Registered</strong> group.</p>', 1, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 1, 2, 5, 6, 2, 0, '{\r\n"group_web_address":"",\r\n"group_email_address":"",\r\n"group_owner":"0",\r\n"group_collection_id":"0"\r\n}', '{}', '{}', 'en-GB', 0, 4),
